    public int shortestBridge(int[][] grid) {



        Set<Point> fromIsland = new HashSet<>();
        boolean found = false;
        for(int i=0; i<grid.length;i++){
            for(int j=0; j<grid[i].length;j++){
                if(grid[i][j] == 1){
                    getArea(grid, i, j, new HashSet<>(), fromIsland);
                    found = true;
                    break;
                }
            }
            if(found) break;
        }

      /*
       for(int i=0; i<grid.length;i++){
            for(int j=0; j<grid[i].length;j++){
                if(fromIsland.contains(new Point(i,j))){
                    System.out.print(" 1 ");
                }else{
                    System.out.print(" 0 ");
                }
            }
            System.out.println();
        }
        System.out.println(fromIsland);
*/
        //bfs

        int dist=-1;
        Queue<Point> queue = new LinkedList<>(fromIsland);
        Set<Point> visited = new HashSet<>();
        while(!queue.isEmpty()){
            int size = queue.size();
            while(size > 0){
                Point p = queue.poll();
                size--;
                if(!fromIsland.contains(p) && grid[p.x][p.y] == 1){
                    return dist;
                }

                addToQueue(p.x+1,p.y,queue,fromIsland, visited,grid);
                addToQueue(p.x-1,p.y,queue,fromIsland, visited,grid);
                addToQueue(p.x,p.y+1,queue,fromIsland, visited,grid);
                addToQueue(p.x,p.y-1,queue,fromIsland, visited,grid);

            }
            dist++;

        }


        return dist;
    }

    void addToQueue(int x, int y,Queue<Point> queue, Set<Point> fromIsland, Set<Point> visited, int[][] grid){

        if(fromIsland.contains(new Point(x,y)) || visited.contains(new Point(x,y))) return;
        if(x < 0 || y < 0 || x >= grid.length || y >= grid[x].length) return;

        visited.add(new Point(x,y));
        queue.add(new Point(x,y));
    }


    public int getShortestPath(int[][] grid, int x, int y,Set<Point> area, Set<Point> visited, int[][] dp){
        if(area.contains(new Point(x,y)) || visited.contains(new Point(x,y))) return Integer.MAX_VALUE;
        if(x < 0 || y < 0 || x >= grid.length || y >= grid[x].length) return Integer.MAX_VALUE;

     //   if(dp[x][y] != 0) return dp[x][y];
        if(grid[x][y] == 1) return 0;

        visited.add(new Point(x,y));
        int ans = Integer.MAX_VALUE;

        ans = Math.min(ans, getShortestPath(grid, x+1,y,area,visited, dp));
        ans = Math.min(ans, getShortestPath(grid, x-1,y,area,visited, dp));
        ans = Math.min(ans, getShortestPath(grid, x,y+1,area,visited, dp));
        ans = Math.min(ans, getShortestPath(grid, x,y-1,area,visited, dp));

        visited.remove(new Point(x,y));
        if(ans == Integer.MAX_VALUE) {
          dp[x][y] = Integer.MAX_VALUE;
        } else{
            dp[x][y] = ans+1;
        }
        return dp[x][y];
    }


    public void getArea(int[][] grid, int x, int y, Set<Point> visited, Set<Point> area){
        if(visited.contains(new Point(x,y))) return;
        if(x < 0 || y < 0 || x >= grid.length || y >= grid[x].length) return;
        if(grid[x][y] ==0) return;

        visited.add(new Point(x,y));
        area.add(new Point(x,y));
        getArea(grid,x+1,y,visited,area);
        getArea(grid,x-1,y,visited,area);
        getArea(grid,x,y+1,visited,area);
        getArea(grid,x,y-1,visited,area);


    }


